#include "../Bpm.h"#include "ModeSelectorWindow.h"#include <QApplication>#include <QWidget>#include <QPushButton>int main (int argc, char * argv[]) {    // Selector gui    QApplication app(argc, argv);    ModeSelectorWindow modes;    modes.resize(500, 200);    modes.setWindowTitle("MODES MENU");    modes.show();    app.exec();    // Main application gui    Bpm bpm;    // set video file name if video mode    if (modes.getMode() != CAMERA_SOURCE_MODE) {        bpm.setVideoFileName(string(modes.getFileName()->toStdString()));    }    bpm.init(modes.getMode(), FOURIER_MASK_MODE);    // HERE WE GO!!    return bpm.run();}