#include "../Bpm.h"#include "ModeSelectorWindow.h"#include <QApplication>#include <QWidget>#include <QPushButton>int main (int argc, char * argv[]) {    QApplication app(argc, argv);    ModeSelectorWindow modes;    modes.resize(500, 200);    modes.setWindowTitle("MODES MENU");    modes.show();    app.exec();    Bpm bpm = Bpm(modes.getMode(), FOURIER_MASK_MODE, 1.5f);    // HERE WE GO!!    bpm.run();}