#include "./lib/BpmWindow.h"#include "./lib/ModeSelectorWindow.h"#include <QApplication>int main (int argc, char * argv[]) {    // Selector gui    QApplication app(argc, argv);    ModeSelectorWindow modes;    modes.resize(500, 300);    modes.setWindowTitle("MODES MENU");    modes.show();    app.exec();    // Continue only if mode selected    if (modes.getMode() == 0) {        return 0;    }    // Main application gui    Bpm bpm;    // Set video file name if video mode    if (modes.getMode() != CAMERA_SOURCE_MODE) {        bpm.setVideoFileName(modes.getFileName().toStdString());    }    // Save output    if (modes.getSaveOutput()) {        time_t t = time(0);   // get time now        struct tm * now = localtime( & t );        string year = to_string(now->tm_year + 1900);        string month = to_string(now->tm_mon + 1);        string day = to_string(now->tm_mday);        string outputName = year+"-"+month+"-"+day;        bpm.setOutputFilePath(modes.getOutputFolder().toStdString() + "/"+outputName+"-out.avi");    }    bpm.init(modes.getMode(), FOURIER_MASK_MODE);    // HERE WE GO!!    bpm.run();    destroyAllWindows();    QApplication::quit();    return 0;}