#include "./lib/BpmWindow.h"#include "./lib/ModeSelectorWindow.h"#include "./lib/logs/logs.cpp"#include <QApplication>int main (int argc, char * argv[]) {    // Selector gui    QApplication app(argc, argv);    ModeSelectorWindow modes;    modes.resize(500, 300);    modes.setWindowTitle("MODES MENU");    modes.show();    app.exec();    // Continue only if mode selected    if (modes.getMode() == 0) {        return 0;    }    // Main application gui    Bpm bpm;    // Set video file name if video mode    if (modes.getMode() != CAMERA_SOURCE_MODE) {        bpm.setVideoFileName(modes.getFileName().toStdString());    }    // Save output    if (modes.getSaveOutput()) {        string outputName = generateHumanTimestamp();        bpm.setOutputFilePath(modes.getOutputFolder().toStdString() + "/"+outputName+"-out.avi");    }    bpm.init(modes.getMode(), FOURIER_MASK_MODE);    // HERE WE GO!!    bpm.run();    destroyAllWindows();    QApplication::quit();    return 0;}